type SafeParseReturnType<Input, Output> = SafeParseSuccess<Output> | SafeParseError<Input>;

type SafeParseSuccess<Output> = {
    success: true;
    data: Output;
};
type SafeParseError<Input> = {
    success: false;
    error: ZodError<Input>;
};
