```ts
function create_user_schema(ctx: { city_nullable: boolean }) {
  let city_schema = z.string()
  if (ctx.city_nullable) {
    city_schema = city_schema.nullable()
  }

  return z.object({
    name: z.string(),
    address: z.object({
      city: city_schema,
    }),
  })
}
```

