const MyComponent = () => {
  const [id, setId] = React.useState(undefined);

  React.useEffect(() => {
    // Set `id` after 1 second of delay for demo purposes.
    setTimeout(() => {
      setId("123");
    }, 1000);
  }, []);

  const form = useForm({
    resource: "posts",
    id: id,
    queryOptions: {
      // Disable query if id is not defined
